    # structure by fport
    function rx_fport()
    {
        $struct = [];

        # fport 24
        $struct[ 24 ] = [
                #WM-BUS
                [ '_cnf' => [ 'repeat' => false, 'name' => 'wm_bus' ],
                    'length' => [ 'type' => 'uint8'],
                    'c_field' => [ 'type' => 'hex'],
                    'man_id' => [ 'type' => 'hex', 'length' => 2, 'formatter' => ':wmbus_manufacturer', 'formatter_library' => $this->manufacturer_register ],
                    'serial' => [ 'type' => 'hex', 'length' => 4 ],
                    'version' => [ 'type' => 'hex' ],
                    'type' => [ 'type' => 'hex' ],

                    # NOT
                    'status' => [ 'type' => 'byte', 'bits' => [
                        [ 'bit' => '0-3', 'type' => 'decimal', 'parameter' => 'maximum_sf' ],
                        [ 'bit' => 4, 'parameter' => 'sf_too_low', 'formatter' => [ false, true ] ],
                        [ 'bit' => 5, 'parameter' => 'communication_lost', 'formatter' => [ false, true ] ],
                    ], 'when' => [ [ '{packet_length}' => '0f' ] ] ],

                    # OK
                    'payload' => [ 'type' => 'hex', 'byte_order'=>'LSB', 'length' => [ 'device:wm_bus:length' ],
                        'ext' => [ 'mbus-decrypt', [ 'length' => '{wm_bus:length}',
                            'man_id'=>'{wm_bus:man_id>value_raw}',
                            'serial'=>'{wm_bus:serial}',
                            'version'=>'{wm_bus:version}',
                            'type' => '{wm_bus:type}' ] ],
                        'when' => [ [ '{packet_length}' => '>0f' ] ] ],

                ],

            ];

        return $struct;
    }


    /**
     * @return array
     */
    public function tx_fport()
    {
        $tx = self::rx_fport();
        return $tx;
    }

